css:
@-moz-document domain("yinxiang.com"), domain("evernote.com") {

/* 笔记列表标题 */
div.noteSnippetTitle { color:#000 !important;padding: 2px 0 !important; }

/* 笔记摘要列表块 */
div.noteSnippetContent { color:#999 !important; }
span.noteSnippetDate { color:#37a02c !important; }
span.noteSnippetContent { color:#999 !important; line-height:1.2em !important; }


/* 笔记正文标题 */
#gwt-debug-noteTitle { color:#000 !important; font-weight:bold !important; }

/* 左下角广告 */
div[style='position: absolute; overflow: hidden; left: 0px; right: 0px; bottom: 0px; height: 135px;'] { display:none !important; }
div[style='position: absolute; overflow: hidden; left: 0px; top: 0px; right: 0px; bottom: 165px;'] { bottom: 0 !important; }

/* ---------- 滚动条样式 -------- */
::-webkit-scrollbar { width: 10px;height: 10px; }
::-webkit-scrollbar-button:vertical { display:none !important; }
::-webkit-scrollbar-button:vertical:start:increment,::-webkit-scrollbar-button:vertical:end:decrement { display: none; }
::-webkit-scrollbar-track:vertical { background-color: #fff; } /* scrollbar 最底层部分*/
::-webkit-scrollbar-track-piece { background-color: #f7f7f7; } /* scrollbar 可见背景部分 */
::-webkit-scrollbar-track-piece:vertical:start { border-left: 1px solid #f7f7f7; }
::-webkit-scrollbar-track-piece:vertical:end { border-left: 1px solid #f7f7f7; }
::-webkit-scrollbar-thumb:vertical { height: 30px;background-color: #e9e9e9;  } /* 拖动条 */
::-webkit-scrollbar-resizer:vertical { background-color: #ddd; }

}
