css:
@-moz-document domain(livejournal.com) {

.talk-comment  {
margin: 3px auto !important;
}

.talk-comment td[rowspan="2"] {
border: 0px !important;
}

.talk-comment td[bgcolor="#aaccee"], 
.talk-comment td[bgcolor="#99ccff"] {
background-color: #BBDDFF !important;
}

.talk-comment td {
padding: 5px !important;
border: 1px solid #BBDDFF !important;
}

.talk-comment tr:first-child img[alt] {
width: auto !important;
max-height: 50px !important;
}

.talk-comment tr:first-child img[alt]:hover {
max-height: 100px !important;
}

.talk-comment tr:first-child a img {
width: 12px !important;
height: 11px !important;
}

.talk-comment tr:first-child .ljuser a img {
width: auto !important;
height: auto !important;
}

.talk-comment font b:first-child {
font-style: italic !important;
font-size: 15px !important;
}

.ljcmtmanage {
float: left !important;
position: relative !important;
top: 0px !important; left: 0px !important;
bottom: 0px !important; right: 0px !important;
z-index: auto !important;
}

}
