css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("xfire.com") {

a.background, .wide, .advert_top {display: none !important}

/* Forums */

#wgo_onlineusers, .navlinks, #wgo_stats, #wgo_legend, .blockbody, .blockrow {
    background-color: #2e2e2e !important;
    color: white !important;
}

.blocksubhead, .blockfoot {
    background-color: #666666 !important;
    color: #cccccc !important;
}

.time {
    color: #cccccc !important;
}

input, select, .textbox {
    background-color: #666666 !important;
    color: #cccccc !important;
}

}
