@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("metal-archives.com") 
{



/*Table Background to White*/
table,td,tr,p,html,center,TABLE > TBODY > TR > TD
{
  background-color:#FFFFFF !important;
  color:#000000 !important;
  font-family:Calibri,Arial !important;
  font-size:14px !important;
  border-color:black !important;
}






/*Table Headings Styled*/
td.trt
{
  background-color:#000000 !important;
  color:red!important;
  font-family:Calibri,Arial !important;
  font-weight:bold !important;
  font-size:18px !important;
  border-color:black !important;
}



/*Heading of Band on Band Page*/
td.tt
{
  background-color:#222222!important;
  color:red!important;
  font-family:Calibri,Arial !important;
  font-weight:bold !important;
  font-size:24px !important;
  border-color:black !important;
}


 

/*Calibri and White Background*/
body, .genmed
{
  background-color:#FFFFFF !important;
  color:#000000 !important;
  font-family:Calibri,Arial !important;
  font-size:12px !important;
}





}


@-moz-document url-prefix(http://metal-archives.com/board), url-prefix(http://www.metal-archives.com/board)
{

/* Fix the forum board text: */
span
{
  background-color:#FFFFFF !important;
  color:#000000 !important;
  font-family:Calibri,Arial !important;
  font-size:12px !important;
}


}
