css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("geekologie.com"),
domain("thesuperficial.com"),
domain("iwatchstuff.com") {
div#leaderboard, #logo, #advertorial, .admoduleB, div.advertorial-title{
display:none !important;
visibility:hidden !important;}

div#header, #logo{height:35px !important; background-image:none !important}

div#container{width:100% !important;}
/*body{background-image:none !important;}*/
div.admodule, div.skyscraper, div.adblock, div.rectanglead{display:none !important;}

div#mainbody{width:70% !important;}
div#entrycontainer{width:auto !important;}
div.entrybody{width:auto !important;}

div#sidebar{width:30% !important; position:absolute !Important;
	right:0;}

div.navigation{width:50% !important; margin-left:10%;}
div.navigation span{position:relative !important; left:50% !important;}

	
}
