@-moz-document domain(gronkh.de) {
/* removes the black background */
body {
	background: rgb(239, 239, 239) !important;
}
/* removes the round corners of the menu */
#menu {
    border-top-left-radius: 0px !important;
    border-top-right-radius: 0px!important;
    border-bottom: 0px solid rgb(48, 48, 48) !important;
}
/* removes the round corner of the menu title */
#menu .title {
    border-top-left-radius: 0px 0px !important;
}
/* removes the round corners of the buttons in the menu */
#menu li a.drop {
    border-top-left-radius: 0px !important;
    border-top-right-radius: 0px!important;
}
/* removes the large picture slider */
#featured {
    display: none !important;
}
/* removes the facebook like-button .... what ugly thing :3 */
#headbox .fb-like {
    display: none !important;
}
/* removes the small links in the header */
#blinks {
     display: none !important;
}
/* removes the top bar above the header */
ul#nwl {
    display: none !important;
}
/* fixes the headlne */
#headline {
    top: 0px !important;
}

}
