css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("blog.esuteru.com") {
	/* ad block */
	aside#right_col,
	div#headline,
	div.google-user-ad,
	div#ad_rs,
	a#header_logo,
	div.amazon_hot_item_entry,
	div#entry > center,
	div#articleBodyMore_bottom,
	div#entry aside.foot > center,
	div#entry aside.foot div:nth-of-type(3),
	div#entry aside.foot div.amazon-list-view,
	div#entry aside.foot div.amazon,
	div#bg_ads,
	div[class^="google-user-ad"]
	{
		display: none !important;
	}
	/* header */
	#header {
		border: none !important;
	}
	#header div.wrap {
		border: solid 1px #fff !important;
		width: 1000px !important;
	}
	
	
	/* body */
	div#content {
		width: 1000px !important;
	}
	div#main_col {
		border: none !important;
		float: none !important;
		width: auto !important;
	}
	div#main_col > article > section {
		width: 316px !important;
	}
	div#main_col > article > section div.body {
		width: 303px !important;
	}
	div#main_col > article > section div.body img.pict {
		height: auto !important;
		width: 284px !important;
	}
	div#main_col > article > section div.body a.pict_anchor {
		height: 179px !important;
		width: 282px !important;
	}
	div#main_col > article > section div.body b {
		bottom: -140px !important;
		width: 266px !important;
	}
	div#main_col > article > section div.body ul.social {
		right: 22px !important;
	}
	
	/* nabi */
	ul#go_navi {
		display: none !important;
	}
	
	/* footer */
	#footer div.wrap {
		width: 960px !important;
	}
	#footer div.box {
		width: 163px !important;
	}
	
	#footer div.box:before {
		width: 163px !important;
	}
	
	/* entry */
	div#entry{
		margin: 0 auto !important;
		width: 754px !important;
	}
	
	div#entry section.head hr,
	div#entry aside.foot > hr
	{
		display: none !important;
	}
}
