@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.theatheistconservative.com") {


input
{
    -moz-appearance: none !important;
}
input#s
{
    background: #000 !important;
    color: #FFF !important;
}

body {
background: #222 !important;
color: #FFF !important;
}

p {color: #BBB !important;}

div {color: #999 !important;}

a:link {color: #096 !important;}
a:hover {color: #CCF !important;}
a:visited {color: #774 !important;}

div.meta {background: #111 !important;}

#content blockquote {
border-left: 5px solid #BBB !important;}

/*DISQUS STYLES*/

div.dsq-textarea-background {background: #666 !important;}

div.dsq-textarea.dsq-textarea-reply {color: #FFF !important;}
div.dsq-post-tools {background: #111 !important;}
.dsq-button {background: #222 !important;
box-shadow: none !important;
text-shadow: none !important;}

div.dsq-popup-content {
background: #000 !important;
color: #CCC !important;
}
div.powered-by {
background: #111 !important;
color: #BBB !important;
}

ul.dsq-modal-tabs {background: #333 !important;}

input#dsq-field-email {background: #111 !important;}
input#dsq-field-name {background: #111 !important;}
input#dsq-field-website {background: #111 !important;}

a.dsq-commenter-name {color: #696 !important;}
a.dsq-commenter-name:hover {color: #CFC !important;}

input#recaptcha_response_field {background: #000 !important;}

.messageBody {color: #FFF !important;}
}
