css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("learncpp.com") {

/* Overall post display styles */
body {
    background: #0E1612 !important;
}
a { color: #34DF26 !important; }
div#container {
    background: #000 !important;
    color: #FFF !important;
}
.navigation-top { border-top: 4px solid #333 !important; }
.navigation-middle { border-bottom: 4px solid #333 !important; }
.post { border-top: 4px solid #333 !important; }
.post table td { background: #030 !important;
border-bottom: 1px solid #161 !important; }
.post table td:hover { background: #050 !important; }
.post table tr.alt td { background: #020 !important; }
.post table tr.alt td:hover { background: #040 !important; }
.post table img { background: #060 !important; }
.post table th { background: #040 !important;
border: 1px solid #070 !important; }
.post table td { border: 1px solid #070 !important; }
td#footer { background: #020 !important;
border-top: 4px solid #040 !important; }

/* Side widgets */
.widget { 
    border: 4px solid #555 !important;
}
.widget h3 {
    color: #DEDEDE !important;
    border-bottom: 1px solid #555 !important;
}

/* Google Widget */
form table { background: #020 !important; }
input#sbi { background: #000 !important; 
color: #FFF !important; }
form tbody font { color: #0E0 !important; }

/* Syntax for code sections */
.syntaxhighlighter div { background: #0E1612 !important; }
.syntaxhighlighter div table { border: none !important; }

.plain { color: #CCE03F !important; }
.keyword,.color1 { color: #2CC815 !important; }
.comments { color: #BC3636 !important; } 
.string { color: #952BFF !important; }
.preprocessor { color: #15A8DF !important; }
.line { color: #1180C0 !important; }
.functions { color: #DC3F9F !important; }

/* Comment list */
.comment-container { background: #222 !important; }
.children { background: #333 !important; }
.bypostauthor { background: #252 !important;
color: #FFF !important; }
}
