css:
* {
background: #383838 !important;
border-color: #747474 !important;
color: #82b7ff!important;
text-shadow: none !important;
-webkit-box-shadow: none !important;
-moz-box-shadow: none !important;
}

div.res h3 a.yschttl,
a:link {
color: #599fff !important;
}

b,
b *,
strong,
strong *,
a:hover {
color: #ff8712 !important;
}

h1,
h1 *,
h2,
h2 *,
h3,
h3 *,
h3 cite,
h4,
h4 *,
h5,
h5 *,
h6,
h6 *,
div.res h3 a.yschttl:visited,
pre,
a:visited {
color: #ff9228 !important;
}

span.url,
cite,
cite * {
color: #af5f00 !important;
}

li,
i,
i *,
em,
div.res a[href*="http"] {
color: #82b7ff !important;
}

input,
button,
select,
textarea {
background: #383838 !important;
border: 1px solid #747474 !important;
}

a.sa-drawer {
background: #3e3e3e !important;
}

.ico,
.sm-media,
img[alt="toggle search suggestions"],
span.indicator,
span.video-icon,
img[src*="ygmabtlogoimg"],
img[src*="ygmalogoimg"],
img[src*=".gif"],
img[src*="logo"], 
img[src*="icon"], 
img[src*="folder"] {
display: none !important;
}
