css:
@-moz-document domain('game.planetarion.com') {
.container .header {
    background-color: rgba(0, 0, 0, 0.2);
    border-left: 2px solid #2b5973;
    border-right: 2px solid #2b5973;
    border-top: 2px solid #2b5973;
    margin: 0 15px !important;
    padding: 3px;
}
.container .maintext {
    border-left: 2px solid #2b5973;
    border-right: 2px solid #2b5973;
    margin: 0 15px;
}
.container .footer {
    border-bottom: 2px solid #2b5973;
    border-left: 2px solid #2b5973;
    border-right: 2px solid #2b5973;
    margin: 0 15px;
}
.shadedbackground {
    background-color: rgba(0, 0, 0, 0.1) !important;
}
.shadedbackground2 {
    background-color: rgba(0, 0, 0, 0.2) !important;
}
.news_combat table {
    background-color: rgba(0, 0, 0, 0.4) !important; 
}
.news_combat table tr th, .news_combat table tr td {
    border: 1px solid #000 !important;
}
}
