css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document  url-prefix('http://www-10.lotus.com/ldd/') {
	/* Hide unnecessary element */
	div#lotusColLeft,
	div#rare_widget_div_doc,
	div#rzcontrols,
	div.lotusActionBar,
	div.lotusBold,
	div.sectionlotusBold,
	nav#lotusBreadcrumbs,
	hr,
	header.lotusBanner,
	header.lotusTitleBar2,
	footer.lotusFooter
	{
		/*display: none !important;*/
	}
	
	body,
	body.lotusui30_body,
	body.lotusui30_fonts,
	body.oneui30,
	div.lotusMain
	{
		/*background:	#E9F5E9 !important;*/
	}
    
    body {
		font-size: 		1.0em    !important;
    }
	
    .wikiDoc,
    div p, 
    div dd, 
    div li, 
    span.dijitTreeLabel 
	{
		font-family: 	'Proxima Nova', Roboto, 'Helvetica Neue', Helvetica, Arial, Verdana, SimSun, SimHei !important;
		font-size: 		16px   !important;
		line-height:	1.5em   !important;
	}

	table td.code-outline, 
    div pre
	{
		font-family: 	Inconsolata, 'Courier New'   !important;
		font-size: 		16px	!important;
		line-height: 	1.2em	!important;
		white-space:	pre-wrap;	    /* CSS-3 */
		word-wrap:		break-word;
		word-break:		break-all; 
	}

}
