css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url("about:newtab") {
#newtab-margin-top, #newtab-horizontal-margin, .newtab-side-margin, #newtab-margin-bottom, #newtab-grid{
background:black!important;
}
.newtab-thumbnail, .newtab-cell, .newtab-link, .newtab-site{
border-radius:5px!important;
}
.newtab-title{
border-bottom-left-radius:4px!important;
border-bottom-right-radius:4px!important;
background: -moz-linear-gradient(top,  rgba(31,31,31,.885) 0%, rgba(17,17,17,.85) 50%, rgba(1,1,1,.85) 51%, rgba(22,22,22,.85) 100%)!important;
color:White!important;
box-shadow:black 0px -1px 2px!important;
}
.newtab-site{
box-shadow:black 0px -1px 15px!important;
}
}
