@-moz-document url-prefix("https://www.google.com/calendar/") {
#onegoogbar {
  position:absolute;
  width:100%;
  transition:top 0.3s;
  z-index:999;
  
}
#onegoogbar {
  top:-55px;

}

#onegoogbar:hover{
  top:0;
  transition:top 0.5s;
}

