css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix('http://nyheter24.se/filmtipset') {

    /* Gömma saker*/
    #bannerdivtop,#bannerdivright,
    #temp_coreNetworkNavigation,
    body > div:first-child,
    [src*="annons-med-pil.png"] 
        { display: none}
    
    /* Flytta saker */
    #contentWrapper { margin-top: 150px !important; }
    .big_canvas { border: none !important; }
    .big_canvas > table { width: 1057px !important ; }

    /* Ögon-ikon */
    [href$="package=1"],
    #wantedlist_hide
    {
        background-repeat: no-repeat;
        background-image: url('http://liten.besvikel.se/icon_eye.gif')
    }
    
    [href$="package=1"]
    { 
        padding-right: 25px;
        background-position: right top;
    }
    #wantedlist_hide
    {
        background-position: 140px top;
    }
    
    /* Favorit-ikon */
    div.rightlink [href$="page=favorites"],
    [href*="type=favorite"]
    {
        padding-right: 25px;
        background-position: right top;
        background-repeat: no-repeat;
        background-image: url('http://liten.besvikel.se/heart.png')
    }
}
