css:
@-moz-document url-prefix(www.kafic.net), domain(www.kafic.net)

{

body

{

	font-family: Comic Sans, Comic Sans MS, cursive !important;

	background:#000000 !important;

}

#container, .windows-basic-medium, .windows-basic-small, .windows-medium, .panel-basic,

#forum, .forum-post-body, .forum-user-column, .forum_post_right_footer, .forum_post_left_footer,

h3, #forum table, .codetop, .htmltop, .quotemain, .windows-basic,

.windows-small

{

	color:#ffffff !important;

	background-color: #212121 !important;

	border-color:#C2C2C2 !important;

}

.forum-sub th

{

	color:#C2C2C2 !important;

	background-color: #212121!important;

	border-color: #212121 !important;

}


.forum-top_bar small

{

color:#C2C2C2 !important;
}

.top_bar small

{

	color:#C2C2C2 !important;

}




.quotemain

{

	border-color:#212121 !important;

	border-left-width: 1px !important;

	background-color:#000000 !important;

}


h1.title, h2.title, h3.title, h4.title, h5.title, h5, h1.title a:link, h2.title a:link,

h3.title a:link, h4.title a:link, h5.title a:link, h1.title a:visited, h2.title a:visited,

h3.title a:visited, h4.title a:visited, h5.title a:visited, h2 a

{

	color:#C2C2C2 !important;

}

.forum-sub th, .forum-latest-topic a

{

	color: #C2C2C2 !important;






}


#liveboard .panel-basic, .panel-basic, .selector

{

	background:none !important;

}

a:link.user_male, a:visited.user_male

{

	color:#3399FF !important;

}

a:link.user_female, a:visited.user_female

{

	color:#FF3333 !important;

}



.table_info .odd, .odd{

	background:#292929 !important;

}



.border_menu .icon a:hover, .border_menu .icon.current_link a, .border_menu .icon.current_link a:link, .border_menu .icon.current_link a:visited

{

		background-color:#292929 !important;

}

.ul_list a

{

	color:#C2C2C2 !important;

	background-color:#292929 !important;

	border: 1px solid #1c1c1c !important;

}

.ul_list a:hover, .ul_list .current_link a

{

	color:#C2C2C2 !important;

	background-color:#4A4A4A !important;

}












#main {

background: #242424 url(http://img440.imageshack.us/img440/9482/555555555555555555555os0.png)  !important;

}

#row2 {

background-color: #242424  !important;

}

#online_list {

background: #242424 url(http://img232.imageshack.us/img232/7287/11111111111111111111111pj8.png)!important;

}

#broadcast_main {

background-color: #242424 !important;

}

}
