css:
@-moz-document domain("www.navitime.co.jp") {

div#topnews_frame,
div#topics_frame,
div#contentsleft_frame,
div#popular_frame,
div#recommend_frame,
div#cgmrank_frame,
#trandad_frame {display: none;}

div#routemenu_frame {width: 650px;}

div#routemenucontents_transfer,
div#routemenucontents_car,
div#routemenucontents_tnavi {
height: auto;
}

div.routeposition_column {
width: 100% !important;
}

div.routeposition_title {
width: 4em;
font-size: 120%;
}

input.routepostion_text_auth {
padding: 2px 5px;
width: 300px;
}

div.popup {
font-size: 100%;
overflow: normal;
padding: 3px 5px;
width: 150px;
height: 200px;
}

div.popup a.list {
margin: 3px 0;
padding: 2px;
}

div.userDataBtn {
margin: 0 1px;
}

div.userDataBtn img {
width: 24px;
}

}
