css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("mathrubhumi.com") {
body,
#masterDiv,
#bodyMainDiv,
#firstDiv{
  float: none !important;
  margin: auto !important;
  text-align: center !important;
}
iframe,
#headDiv,
#topmiddlebannerDiv,
#rightLinkDiv,
#leftLinkDiv,
#bannerDiv,
#bottomDiv,
#bottomBannerDiv{
  display:none !important;
}
#flashDiv table {
  border: none !important;
}
#iemarquee strong {
  color: #966 !important;
  font-weight: normal !important;
}
#newsDiv,
#newCategoryDiv {
  background-color: #fff !important;
  border-width: 0px !important;
}
.tbborder1 {
    border: 0px solid #abc !important;
}
.tbborder2,
.tbborder3 {
  border:1px solid #abc !important;
  -moz-border-radius: 5px !important;
}
.tbborder3 {
  background-color:#cde !important;
}
.text10 {
  color: #555 !important;
}
.text14,
.text4:link,
.text4:visited {
  color: #666 !important;
}
.text4:hover {
 color: #333 !important;
}
.text5 {
  color: #789 !important;
}
.text5:hover{
   color: #567 !important;
}
img,
embed {
  display:none !important;
}
#newCategoryDiv table img,
#mainNewsDiv table img {
 display:block !important;
 margin-top: 5px !important;
}
#AutoNumber1,
.mooter_ads,
.mooter_branding {
  display:none !important;
}

}
