css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://maps.lt/"), url-prefix("http://www.maps.lt/")
{
  *[id*='banner'],
  #title-right,
  #site ~ div,
  #hb_dijit_WidgetsWhiteSpaceFillerPane_0,
  iframe {
    display: none !important;
  }

  #search-form {
    height: 40px !important;
    padding: 5px 0 !important;
  }

  #search-form-info {
    font-size: 0.8em !important;
  }

  #search-block a {
    font-size: 1.5em !important;
  }
  
  #logo {
    padding: 5px !important;
  }

  body {
    background-position: left -41px !important;
  }

  body.title {
    background-position: left -137px !important;
  }

  .hbInfoWindow {
    opacity: 0.75 !important;
  }
}

@-moz-document url-prefix("http://maps.lt/map/"), url-prefix("http://www.maps.lt/map/")
{
  #top,
  .hbLogo,
  .hbCopyright + span
  {
    display: none !important;
  }
  #app,
  #layout 
  {
    top: 0 !important;
  }
}
