css:
@-moz-document domain('psnprofiles.com')
{
  tr:nth-child(even)
  {
    background-color:rgb(255,255,255)!important;
  }
  table.game_list tbody tr:nth-child(odd)
  {
    background-color:rgb(240,240,240)!important;
  }
  table.game_list tbody tr:nth-child(even) td:first-child
  {
    border-left:4px solid rgb(255,255,255)!important;
  }
  table.game_list tbody tr:nth-child(odd) td:first-child
  {
    border-left:4px solid rgb(240,240,240)!important;
  }
  table.game_list tbody tr.completed td:first-child
  {
    border-left:4px solid rgb(0,150,0)!important;
  }
  table.game_list tbody tr.platinum td:first-child
  {
    border-left:4px solid rgb(102,127,178)!important;
  }
  table.game_list tbody tr.cheated td:first-child
  {
    border-left:4px solid rgb(237,31,31)!important;
  }
}
