css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url("about:newtab") {

#newtab-scrollbox:not([page-disabled]), 
#newtab-scrollbox { 
background: url('http://i.imgur.com/SSEK9.jpg') 100% 100% !important; 
background-repeat: no-repeat !important; 
background-attachment: fixed !important; 
background-position: center !important; 
-webkit-background-size: cover !important;
-o-background-size: cover !important; 
background-size: cover !important;  
overflow-y: hidden !important; 
min-height: 100% !important; 
min-width: 1024px !important; 
width: 100% !important; 
height: auto !important; 
}

.newtab-cell:hover:not(:empty), 
.newtab-control-pin:-moz-locale-dir(ltr):hover, 
.newtab-control-block:-moz-locale-dir(rtl):hover, 
.newtab-control-block:-moz-locale-dir(ltr):hover, 
.newtab-control-pin:-moz-locale-dir(rtl):hover { 
-moz-transition: all 0.2s ease-out 0s !important; 
-webkit-transition: all 0.2s ease-out 0s !important; 
-o-transition: all 0.2s ease-out 0s !important;  
box-shadow: 0 0 15px #C72026 !important;
}

html, 
window, 
#newtab-scrollbox, 
span.newtab-title { 
cursor: url(http://cur.cursors-4u.net/cursors/cur-7/cur610.cur), progress !important; 
}

.newtab-thumbnail[dragged], 
.newtab-link:-moz-focusring > .newtab-thumbnail, 
.newtab-site:hover > .newtab-link > .newtab-thumbnail, 
.newtab-title, 
#newtab-toggle { 
cursor: url(http://cur.cursors-4u.net/cursors/cur-7/cur608.cur), progress !important; 
}

.newtab-control-pin:-moz-locale-dir(ltr), 
.newtab-control-block:-moz-locale-dir(rtl), 
.newtab-control-block:-moz-locale-dir(ltr), 
.newtab-control-pin:-moz-locale-dir(rtl) { 
cursor: url(http://cur.cursors-4u.net/cursors/cur-7/cur611.cur), progress !important; 
}

.newtab-thumbnail { 
background-position: top !important; 
-webkit-background-size: cover !important;
-o-background-size: cover !important; 
background-size: cover !important;
background-size: 100% 156px !important;
opacity: 0.2 !important;  
-khtml-opacity: 0.2 !important; 
max-height:  83% !important;
}

.newtab-thumbnail:hover { 
background-position: top !important; 
-webkit-background-size: cover !important;
-o-background-size: cover !important; 
background-size: cover !important;
background-size: 100% 156px !important;
opacity: 1.0 !important; 
-khtml-opacity: 1.0 !important;   
-moz-transition: all 0.2s ease-out 0s !important; 
-webkit-transition: all 0.2s ease-out 0s !important; 
-o-transition: all 0.2s ease-out 0s !important;  
max-height:  83% !important;
}

.newtab-title { 
text-align: center !important; 
text-shadow: #000000 0px 0px 3px !important; 
}

#newtab-toggle:hover { 
background: url('http://i.imgur.com/eDMcb.png') 50% 50% no-repeat !important; 
-moz-transition: all 0.2s ease-out 0s !important; 
-webkit-transition: all 0.2s ease-out 0s !important; 
-o-transition: all 0.2s ease-out 0s !important; 
box-shadow: 0 0 15px #C72026 !important; 
}

}
