css:
@-moz-document url("chrome://speeddial/content/speeddial.xul"){
	
	.speeddialThumb {
		background: -moz-linear-gradient(top,  #ffffff 0%, #ededed 100%);
		margin:20px 6px 0px 6px !important;
		border:0px #445f74 solid;
		padding:9px;
		-webkit-box-shadow:  0px 0px 3px 3px rgba(0, 0, 0, 0.1);
		box-shadow:  0px 0px 3px 3px rgba(0, 0, 0, 0.1);
		border-radius: 3px;
		-moz-transition: margin 0.2s ease-in-out;
	}
	
	.speeddialThumb:hover {
		border:9px #445f74 solid;
		padding:0px;
		margin:15px 6px 5px 6px !important;
	}
	
	.speeddial-top-middle, .speeddial-top-left,.speeddial-top-right, .speeddial-middle-left,.speeddial-middle-middle,.speeddial-middle-right, .speeddial-bottom-left,.speeddial-bottom-middle,.speeddial-bottom-right {
		display:none;
	}
	
	#speeddialWindow {
		background: #dcdfe4 url(http://farm4.staticflickr.com/3219/3001691313_54b7b7ae1d_o.jpg) top center repeat !important;
	}
	
	.speeddial-container{
		margin:0 !important;
		border:0 !important;
		background-color:  transparent  !important;
	}

}
