css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("dm5.com") {

/* ver1.1 : 全宽游览 */

#fengxiang2,.cl970,.view_ts,.view_bt > li,
#act,
 .view_bt > h1,.view_bt > .juh,
 #fengxiang,#adjs_id,#ShowDIV,div[id^=A],.cl760
 {display:none !important;}
}
#logo{margin-top:0 !important;}
#logo img{height:40px;}
#hd{height:auto !important;width:auto !important;overflow:hidden;}
#header{width:auto !important;}
#dm_dh{float:right !important;}
#sos{-moz-transform-origin:top center 0;
-webkit-transform-origin:top center 0;
    -moz-transition:height .4s ease-in-out, -moz-transform .4s ease-in-out;
-webkit-transition:height .4s ease-in-out, -webkit-transform .4s ease-in-out;
    -moz-transform:translateZ(0);
-webkit-transform:translateZ(0);
    height:15px !important;overflow:hidden;}
#sos:hover{overflow:hidden; height:90px !important;}

.ff{
    -moz-transition:height .2s ease-in-out, -moz-transform .2s ease-in-out;
-webkit-transition:height .2s ease-in-out, -webkit-transform .2s ease-in-out;
     -moz-transform:translateZ(0);
-webkit-transform:translateZ(0);
     height:0px !important;overflow:hidden;
     float:right;
}
#hd:hover .ff{height:40px !important;}

/*Index width*/
#index_mian{position:relative;width:auto !important;}
#index_mian #index_left{width:auto !important;display:table-cell;float:none !important;}
.inkk{overflow:hidden;}
