css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain(mail.google.com)
{
  /* quick contacts rounded card */
  table[cellspacing='0'][cellpadding='0'][style^='border: 1px solid rgb(153, 153, 170)']
    {-moz-border-radius: 8px !important;}

  table[cellspacing='0'][cellpadding='0'][style^='border: 1px solid rgb(153, 153, 170)'] 
  td[style^='padding: 5px; background: rgb(193, 217, 255)']
  {
    -moz-border-radius-topleft: 7px !important;
    -moz-border-radius-topright: 7px !important;
  }

  /*button layout in quick contacts cards*/
  button.ab[id^=mail_] > div {padding: 1px 0px 0px 0px !important;}
  button.ab[id^=itc_] > div {padding: 1px 0px 0px 0px !important;}
  button.ab[id^=chat_] > div {padding: 1px 0px 0px 0px !important;}
  button.ab[class$=a] > div {padding: 2px 0px 1px 0px !important;}

  button.ab[id^=mail_] > div {width: 90px !important;}
  button.ab[id^=itc_] > div {width: 90px !important;}
  button.ab[id^=chat_] > div {width: 90px !important;}
  button.ab[class$=a] > div {width: 90px !important;}
}
