css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.i-programmer.info") {
 
        /* Removes terrible design choices */
        td.greyline + td, td.greyline{
            display: none;
        }
        
        /* Expands page to sanity */
        #wrapper{
            max-width: inherit !important;
        }
        
        
        /* Expands out content to page width */
        table.contentpaneopen, table.nopad{
            width: auto !important;
        }
        
        /* Removes eyesore */
        a[target=_Blank]{
            display:none;
        }
        
}
