css:
@-moz-document url-prefix("http://plus.google.com/"), url-prefix("https://plus.google.com/") {
/*
main content
*/

.kI /*navigation container*/
{
width: 100%!important;
}


.Fm /*share what's new container*/
{
width: 90% !important;
}

.Ff /*share whats new textbox*/
{
width: 100% !important;
}


.bR /*content stream container*/
{
position: relative !important;
top: 200px !important;

margin-bottom: 200px !important;

width: 100% !important;
}

.y0b /*content stream container 2*/
{
width: 95% !important;
}

.Fq /*content stream container 2*/
{
width: 95% !important;
}

.Tg /*content stream post*/
{
width: 90% !important;
}

.dv iframe
{
max-height: 500px !important;
}

.Yj /*picture container*/
{
min-width: 248px !important;
max-width: 100% !important;
width: auto !important;
}







/*
side bar to top
Moves "Trending on Google+", "You Might Like", "Events", etc to the top of the stream
*/

.hxa /*'ad' container*/
{
height: 180px !important;
width: 100% !important;
margin-top: 80px !important;

position: absolute !important;
left: 0px !important;
top: 0px !important;

float: left !important;

display: block !important;
overflow: auto !important;
}

.hu /*'ad' container 2 (search)*/
{
width: auto !important;
}

.Ala /*'ad' container 2*/
{
position: static !important;

margin: 0px !important;
padding: 0px !important;

height: auto !important;
width: 99% !important;

min-height: 150px !important;
top: 0px !important;
}

.Tr /*'ad' div*/
{
width: 256px !important;

position: inherit !important;
top: 0px !important;

float: left !important;
margin-left: 20px !important;
}

.Om /*'ad' div 2*/
{
margin-bottom: 10px !important;
}


}

@-moz-document url-prefix("http://plus.google.com/"), url-prefix("https://plus.google.com/") {
/*
user page specific
*/

.oX /*title at top (name and groups)*/
{
width: 800px !important;
}


/*
side bar to top
Moves In Circles, etc to the top of the stream
*/

.vcard .Vra .qga .Vka /*side container (includes avatar, and side bar)*/
{
width: 100% !important;
height: 200px !important;

left: 0px !important;

margin-top: 200px !important;
}

.vcard .Vra .qga .Vka .hxa /*'ad' container*/
{
height: auto !important;
width: 850px !important;
margin-top: 50px !important;
}

.vcard .Vra .qga .Vka .lCa /*avatar*/
{
margin-left: auto !important;
margin-right: 22px !important;
margin-top: -200px !important;
}

.vcard .Vra .qga .Vka .Oka /*info below avatar*/
{
width: 256px !important;

margin-left: auto !important;
margin-right: 25px !important;
}

.vcard .Vra .qga .Vka .hxa .hu
{
padding-top: 0px !important;
}

.NX /*Circle info*/
{
margin: 0px !important;
padding-top: 0px !important;
padding: 0px !important;

left: -20px !important;

max-height: 200px !important;
height: auto !important;
width: 850px !important;

display: block !important;

overflow: auto !important;
}

.vcard .Vra .qga .Vka .IT /*block user, etc*/
{
margin-left: auto !important;
margin-right: 25px !important;

width: 256px !important;
}

.vcard /*page container*/
{
width: 100% !important;
}

.vcard .Vra /*page container 2*/
{
width: 100% !important;
}

.vcard .Vra .qga /*content stream*/
{
margin-top: 250px !important;

width: 100% !important;
}
}
