css:
@-moz-document domain("comunio.de") {
* {background-color:#FFFFFF !important; color:#000000 !important; border-color:#999999 !important; }
html, body { background-color:#eee !important; }
#top, #topwhite { display:none; }
#center > * { margin:0px !important; border:1px #999 solid; -webkit-box-shadow: 5px 5px 5px 0px rgba(123, 123, 123, 1);
box-shadow: 5px 5px 5px 0px rgba(123, 123, 123, 1); }
#submenu-con, #content { margin:0px; border-bottom: none; border-top: none;}
#footer { border-top: none; }
.subnavi, .subnavi * { background-color:#ddd !important; }
#advertising-right { display:none }
#advertising-top, .spacer20px { display:none }
#title {width:818px; text-align:center; margin-top:10px;}
#title, #title * {background-color:#eee !important}
.menushadow {display:none;}
.button02, .newbutton, .newsubmitbutton, .button01 { border:1px #999 solid !important; background-image:none !important; }
.newbutton, .newsubmitbutton, .button02, .button01, .button01 *, .button02 * {background-color:#eee !important;}
#mainmenu-con { margin-top: 5px !important;  background-image:none !important; }
tr:first-child { background-color:#f9f9f9 !important; }
tr:first-child * { font-weight:bold; background-color:#f9f9f9 !important; }
.mainnavactiv a, .mainnavactiv a:hover { background-image:none !important; background-color:#ddd !important; }
.spielerwahl { display: none; }
#lineup_bg * { background-color:transparent !important; }
}
