css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain('hackforums.net'){
    .logo{
        display:none !important;
    }
    .menu ul{
        background:none !important;
    }
    #container{
        background:rgba(10,10,10,0.9) !important;
        border:none !important;
    }
    .thead{
        background:#767676 !important;
    }
    .tfoot{
        background:#767676 !important;
    }
    .tcat{
        background:#ababab !important;
    }
    .shadetabs li a{
        background-image: none !important;
        background:#434343 !important;
        border-radius: 0px 0px 0px 0px !important;
        box-shadow:0px 1px 0px 0px rgb(44, 228, 239) inset !important;
        padding-left:15px !important;
        padding-right:15px !important;
        text-shadow:none !important;
        font-family:Tahoma, Geneva, sans-serif !important;
    }
    .bitButton{
        background-image: none !important;
        background:#434343 !important;
        border-radius: 0px 0px 0px 0px !important;
        box-shadow:0px 0px 0px!important;
        border: 1px solid #456163 !important;
        padding-left:10px !important;
        padding-right:10px !important;
        text-shadow:none !important;
        font-family:Tahoma, Geneva, sans-serif !important;
    }
    .active{
        display:none !important;
    }
    .menu ul{
        padding-top:5px !important;
        height: 35px !important;
    }
    #panel{
        border: 1px solid #8fdfe4 !important;
    }
    .bottommenu{
        border: 1px solid #8fdfe4 !important;
    }
    .selected{
        color:#a1e9ee !important;
    }
    .tborder{
        background: #547072 !important;
    }
}
