css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix(http://youtube.com),
url-prefix(https://youtube.com),
url-prefix(https://www.youtube.com),
url-prefix(http://www.youtube.com) {

/* Start version 1.0 */

#masthead-user-display { display: none !important; }
#masthead-nav { display: none !important; }
#logo-container { display: none !important; }
#watch-player { margin: auto !important; }
#watch-headline-container { text-align: center !important; }
#watch-headline-user-info { display: none !important; }
#watch-sidebar { display: none !important; }
#watch-panel { float: none !important; margin: auto !important; }
#comments-view { display: none !important; }
#footer { display: none !important; }
#watch-like-unlike { display: none !important; }
#watch-share { display: none !important; }
#watch-flag { display: none !important; }
#masthead-search-bar-container { padding: 0 !important; }
#masthead-search { width: 640px !important; margin: auto !important; }

/* Start version 1.1 */

#guide { display: none !important; }
#watch7-action-buttons { display: none !important; }
.yt-subscription-button { display: none !important; }
#watch7-discussion { display: none !important; }
#watch7-sidebar { display: none !important; }
#watch7-content { margin: auto !important; float: none !important; }
#watch7-player { margin: auto !important; }
.guide-collapsed #watch7-video, .guide-collapsed #watch7-main, .guide-collapsed #watch7-playlist { left: 0 !important; }

/* Start version 1.2 */

#footer-hh-container { display: none !important; }
#content-container,
#baseDiv,
#page-container { padding-bottom: 0 !important; }
#yt-masthead { margin-left: 0 !important; margin-right: 0 !important; width: auto !important; min-width: auto !important; }
#yt-masthead-content #masthead-upload-button-group,
#yt-masthead-signin { display: none !important; }
.site-left-aligned #yt-masthead-content { margin: 0 auto !important; }
#watch7-video-container { padding-left: 0 !important; background: none !important; }
#watch7-video { margin: 0 auto !important; width: auto !important; }
#watch7-main-container { padding: 0 !important; }
#watch7-main { margin: 0 auto !important; width: auto !important; }
#watch-description-extra-info { display: none !important; }
#watch-description-clip { margin: 0 auto !important; }
#watch-description-extras { display: none !important; }
#watch7-user-header > span.yt-uix-button-subscription-container { display: none !important; }
#watch7-action-panels { clear: both !important; bottom: 8px !important; }
#watch7-branded-banner { display: none !important; }
#watch-description.yt-uix-expander-collapsed #watch-description-content { margin-bottom: 0 !important; }
#watch-description.yt-uix-expander-collapsed #watch-description-content.extra-info { height: auto; }
#page.watch, #page.watch #content, .exp-new-site-width #page.watch, .exp-new-site-width #page.watch #guide + #content { padding-bottom: 0 !important; }

/* Start version 1.2.1 */

.yt-uix-clickcard-card { display: none !important; }

/* Start version 1.2.2 */

#watch7-playlist-container { width: 854px !important; margin: 0 auto !important; }
#watch7-playlist-data { padding: 0 !important; left: 107px !important; }
.watch-wide #watch7-playlist-data { left: 0 !important; }

/* Start version 2.0 */

#page,#content { margin: 0 !important; }
.primary-col { margin: 0 auto !important; display: block !important; }
.secondary-col { display: none !important; }
.yt-uix-pager { margin: 0 auto !important; display: table !important; }

/* Start version 2.0.1 */
#footer-container { display: none !important; }

/* Start version 2.0.2 */
#player { padding: 0 !important; }
#player-api { margin: 0 auto !important; }

/* Start version 2.0.3 */

.site-left-aligned #yt-masthead-container, .site-left-aligned #masthead-appbar, .site-left-aligned #footer-container, .exp-new-site-width #ticker, body #sb-wrapper { min-width: 0 !important; }

/* Start version 2.1.1 */

#watch-discussion { display: none !important; }

/* Start version 2.1.2 */
#playlist { display: table !important; margin: 0 auto !important; padding-left: 90px !important; }
#player { margin-top: 0 !important; }
#watch7-subscription-container { display: none !important; }

/* Start version 2.1.3 */
#watch7-main { min-width: 0 !important; }

}

@-moz-document url(http://youtube.com/),
url(https://youtube.com/),
url(http://www.youtube.com/),
url(https://www.youtube.com/) {

/* Start version 2.0 */

#page-container { display: none !important; }
#yt-masthead-container { background: none !important; border: none !important;}
#yt-masthead-content:before { content: url('http://i.imgur.com/u1Ist.png'); }

/* Start version 2.1.0 */

#yt-masthead-container { padding: 0 !important; position: absolute !important; left: 50% !important; top: 50% !important; margin-left: -320px !important; margin-top: -74px !important; }

}
