css:
@-moz-document url-prefix("http://www.newsmth.net/")
{
/*左侧栏悬浮*/
html > body > aside#menu.m-hide
{position:fixed!important;}

/*顶部和sogou广告去除*/
html > body > header#top_head,
div#sogou_banner
{display:none!important;}

/*顶部字体设置*/
section#body>div.t-pre>div.t-btn>a,
section#body>div.b-head>span.n-right,
section#body>div.b-head>span.n-right>a,
section#body>div.b-head>span.left
{font-size:8pt!important;}
}
