css:
@-moz-document url-prefix("http://www.tumblr.com/blog"), url-prefix('https://www.tumblr.com/blog')   {
#posts li.post.is_reblog {
display:none;
}
#posts li.post.is_reblog.queued {
display:block !important;
}
.full_answer_container_wrapper {
display:none !important;
}
}

@-moz-document url-prefix("http://www.tumblr.com/dashboard"), url-prefix('https://www.tumblr.com/dashboard')   {
#posts li.post.is_mine {
display:none;
}
#new_post {
display:block !important;
}
.full_answer_container_wrapper {
display:none !important;
}
}
