css:
@-moz-document domain("vimeo.com") {
html, body, tbody, thead, th, tr, td, blockquote, li, ul, h1, h2, h3, h4, h5, h6, font, strong, p, form
{
 background: none !important;
}

#main
{
background: url('http://www.runemolnes.com/assets/images/rm-nettside-00-footer.png') center repeat-x,
       -webkit-linear-gradient(top, rgba(0,0,0,1) 0%,rgba(0,0,0,1) 800px,rgba(0,0,0,0) 1500px,rgba(0,0,0,0) 100%),
       url('http://subtlepatterns.com/patterns/dirty_old_shirt.png'); 

background: url('http://www.runemolnes.com/assets/images/rm-nettside-00-footer.png') center repeat-x,
          -moz-linear-gradient(top, rgba(0,0,0,1) 0%,rgba(0,0,0,1) 800px,rgba(0,0,0,0) 1500px,rgba(0,0,0,0) 100%),
          url('http://subtlepatterns.com/patterns/dirty_old_shirt.png'); 


background-position: 0px -50px;
}

#site_header nav > ul > li > ul, #site_header .submenus > ul
{
background: none !important;
padding: 10px !important;
}

nav strong
{
color:#fff !important;
}

#site_header
{
 background: none !important;
 border:none !important;
}

#logo
{
 background: #5590A5;
 border-radius: 0em 0em 1em 1em;
 padding: 10px 0;
 margin: -11px 0;
}

#logo:hover
{
 background: #4BF !important;
 box-shadow: 0 0 10px #5590A5 !important;
}
select, button
{
 background: black !important;
}

#tools, .comment
{
 background-color: black !important;
}


#tabs a.selected,
#tabs a.selected:hover
{
 background: #5590A5 !important;
 border: #5590A5 !important;
 color: black !important;
}

#tabs a
{
 border: none!important
}

.boxed, #tabs
{
 background: none !important;
 border: none !important
}

#tabs a:hover.selected
{
 box-shadow: 0 0 10px #5590A5;
}

#content a:hover
{
color: #4BF !important;
}

#inbox_ad,
#channel_header_back,
#channel_header
{
 display:none;
}

#comments article,
.tags,
#tools
{
 border: none !important;
 border-top: 1px solid #2B2B2B !important;
}

#search_form input[type="text"]:focus,
#search_form input[type="text"]:focus
{
box-shadow: 0 0 10px #5590A5;
}



select, 
button, 
#tools, 
.comment
{
 background: none !important;
}

#info #page_header h1
{
 color: #5590A5 !important;
}

#clip
{
 overflow:visible !important;
}

/* */
#clip:hover > #info
{
opacity: 0.5!important;
}
/* */

#info .description.collapsed::after
{
background:none !important;
}

iframe
{
 display:none;
}

#content a
{
 color: #8DA1BB !important;
}

#tabs a.selected,
#content .btn,
#content a,
#logo,
.boxed
{
  -webkit-transition: all 0.3s ease-in-out; 
     -moz-transition: all 0.3s ease-in-out; 
       -o-transition: all 0.3s ease-in-out; 
        -ms-transition: all 0.3s ease-in-out;
}

#tabs a.selected
{
background: #345!important;
border-radius: 5px;
color:#9CA4B1 !important;
 
}

.col_small .block
{
overflow: visible !important;
}

#content a:hover.btn
{
 color: black !important;
 box-shadow: 0 0 10px #5590A5;
}

#tabs a:hover.selected
{
background: #4BF!important;
color: black !important;
}

#info
{
 background: none !important;
 border: none !important;
}

textarea, pre, input, code

{
 background: #333333 !important;
}

#comments #show_all_comments::after
{
 background-image: none !important;
}

#comments #show_all_comments::before
{
 background-image: none !important;
}

#comments #show_all_comments .show_all_link a
{
 background-color: #1F1F1F !important;
}
/* THIS is the experimental scroll bar for Chrome */
::-webkit-scrollbar {
width: 4px;
}

::-webkit-scrollbar-thumb {
background: rgba(0, 0, 0, 0.4);
border-right: 4px solid rgba(17, 34, 51, 1);
}
/* END of the experimental scroll bar for Chrome */
}
