css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("youtube.com") {
#watch-video-container, #filter-dropdown, #upload-button-menu, #gh-checklist, .feed-list-item, .primary-header-contents, .secondary-header-contents, .yt-grid-box, .scrolldetect, .action-panel-content, .spf-nolink, .context-link, #page, .tab-content-body, .gecko-17, .from, .medium, .checkbox, .pointer, .flash-player, .col-select, .date, .yt-admin-h2, .buttonbar {
    background: #333 !important;
    color: #eee !important;
}
#watch-headline-container, #footer-container, .branded-page-v2-primary-col, #page-container, .watch-view-count, #masthead-container, td, #watch-main-container, html, .feed-page, #content-container, .clearfix {
    background: #2C2C2C !important;
    color: #eee !important;
}
a, b, #eow-title, p, strong, yt-uix-button-text, .title, h2, .subject {
    color: #eee !important;
}
.comment, .related-video, .feed-item-outer, .subject, .video-list-item-link, .yt-uix-tile, .playlist-bar-item, .profile-view-module, .yt-c3-grid-item, #tab-inbox, #tab-messages, #tab-videos, #tab-responses, #tab-invites, #tab-comments, #tab-addressbook, #tab-sent {
    border: 0px !important;
    background: #333 !important;
    border: 1px solid #555 !important;
    border-radius: 4px !important;
}
.selected {
    border: 0px !important;
    background: #666 !important;
    border: 1px solid #555 !important;
    border-radius: 4px !important;
}
.watch-sparkbar-likes, .video-extras-sparkbar-likes {
    background: green !important;
}
.watch-sparkbar-dislikes, .video-extras-sparkbar-dislikes {
    background: red !important;
}
a, b, li, p, h1, h2, h3, h4, span, div {
    text-shadow: none !important;
}
.secondary-pane {
    width: 32% !important;
}
}
