css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("facebook.com") {
#mainContainer, ._li, #contentArea, .clearfix, .UFIRow, .-cx-PRIVATE-fbLayout__root, .fixed_elem, .uiTypeahead, #facebook {
    background: #2C2C2C !important;
}
a, .messageBody, div, .info, .blueName, td, .token, .passiveName, .mhs, .uiStreamMessage, .messageBody, .UIImageBlock_Content, .inputtext, .textInput, .adboard_title {
    color: #eee !important;
}
.inputtext, #contentCurve, .timelineUnitContainer, .externalShareContent, h6 {
    background: #333 !important;
}
.storyContent, .item, .uiHeader, .btnLink, .fbFeedTickerStory, .ufiItem, .connect_widget, .connect_widget_number_cloud {
    border: 0px !important;
    background: #333 !important;
    border: 1px solid #555 !important;
    border-radius: 4px !important;
}
.fbConnectWidgetTopmost {
    border: 1px solid #555 !important;
    border-radius: 4px !important;
}
.uiStreamStory, .connect_widget_like_button, #mainContainer, #contentArea, .composerOpen, #contentCurve, .ego_unit_container, #rightCol, .ego_section, .newsFeedComposer, .rhcFooterWrap {
    border: 0px !important;
}
}
