css:
/* BODY MODS BY MULES */

body.custom
{
background-color: #505253 !important;
}

/* HEADER MODS BY MULES */

.custom #header
{
height: 225px;
background-color: #2b2d5e !important;
background: url('http://i.imgur.com/ge1v6.png') left top no-repeat;
}

/* HEADER NUDGE LOGO-TAGLINE MODS BY MULES */

.custom #logo, 
.custom #tagline 
{
margin-left: 522px;
}

/* MENU NAV MODS BY MULES */

.custom .menu
{
background-color: #383847 !important;
}

body { background: #0F0F0F none !important }
a { text-decoration: none !important }



td.tableHead {
   text-transform: uppercase !important;
   background: #222 none !important
}

td.tableCat, td[bgcolor*="001326"] { background: #111 none !important }

td[class*="tableCol"], td.tableColA, td.tableColB { background: #0F0F0F none !important }






td[bgcolor*="002245"] { background-color: #222 !important }
td[bgcolor*="000f1f"] { background-color: #0F0F0F !important }
td[bgcolor*="003366"], table[bgcolor*="003366"] { background-color: #333 !important }


td.tableHeadLarge { background: #222 none !important } 


td.pName {
   background-color: #131313 !important;
   border-color: #333 !important
}


table.pDateBar {
   background: #151515 none !important;
   border-bottom: 1px solid #333 !important
}


td.pBody {
   background: #0F0F0F !important;
   border-color: #333 !important
}


table.pFoot {
   background: #151515 none !important;
   border:1px solid #333 !important
}


div.pOnline { color: #666 !important }

a.pButton, a.pButton:visited {
   -moz-border-radius: 2px !important;
   -webkit-border-radius: 2px !important;
   border-radius: 2px !important;
   background: #324f6c none !important;
   border: 1px solid #446585 !important;
   margin-left: 5px !important;
}

a.pButton:hover {
   background: #FF5A00 !important;
   border: 1px solid #FF7C34 !important;
   color: #FFF !important
}


td[class*="fField"] { background: #0F0F0F none !important }
