css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("userstyles.org") {
html, #search-terms, div, .TextBox, #related, nav, code, pre {
    background: #2C2C2C !important;
}
p, h1, h2, li, input, dt, tr, dl, .Announcement, .MenuTitle, textarea, .Tabs, div {
    color: #eee !important;
}
a, span {
    color: steelblue !important;
}
#search, header, .Item, .Box, li, #Form_Discussion {
    background: #333 !important;
    border: 1px solid #555 !important;
    border-radius: 4px !important;
}
#hoverNav {
    background: transparent !important;
}
a.Title {
    background: #333 !important;
    border: 1px solid #555 !important;
    border-radius: 4px !important;
    padding-left: 2px !important;
    padding-right: 2px !important;
}
table.author-styles > tbody > tr, textarea {
    background: #333 !important;
}
h4 {
    border: 0px !important;
}
#new-setting input, .TabLink, .Category, .BigButton, .Button, input:not([type="button"]):not([type="checkbox"]):not([type="submit"]):not([type="reset"]), select, input[type="file"] > input[type="text"], #search-submit {
	-moz-appearance: none;
	-webkit-appearance: none;
	font-size: 9pt ! important;
	color: #fff !important;
	background: #333 !important;
	border-color: gray !important;
	border: 1px solid;
	border-radius: 2px !important;
}
input[type="submit"], input[type="button"], select {
	background: #444 !important;
	color: #aaa !important;
	border: 1px solid gray;
	-moz-appearance: none;
	-webkit-appearance: none;
	font-size: 9pt !important;
}
.CommentButton {
    text-shadow: none !important;
} 
}
