css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix('http://www.tumblr.com') {

/*sets text color for ALL text*/

/*

* {

color:cddbdb(255, 255, 255, 1.0) !important;

}

*/

/*BIG BACKGROUND begin - background for the entire screen*/

body {

background:url("<a href="http://tinypic.com?ref=be974g" target="_blank"><img src="http://i46.tinypic.com/be974g.jpg" border="0" alt="Image and video hosting by TinyPic"></a>") no-repeat fixed !important;

background-color: cddbdb(44,71,98, 0.95) !important;

background-position: 0% 0% !important;

background-size: 100% 100% !important;

}

/*BIG BACKGROUND end*/

/*LOGO begin - tumblr logo*/

#logo {

width: 0px !important;

height: 0px !important;

background:url("<a href="http://tinypic.com?ref=be974g" target="_blank"><img src="http://i46.tinypic.com/be974g.jpg" border="0" alt="Image and video hosting by TinyPic"></a>") !important;

width: 196px !important;

height: 44px !important;

}

/*LOGO end*/

/*ICON BAR begin - new post icons bar - graphic replacement*/

img[src*="http://assets.tumblr.com/images/new_post_icons_no_labels.png"]{

opacity: 0.00 !important;

}

#new_post{

background-image: url(http://assets.tumblr.com/images/new_post_icons_no_labels.png) !important;

background-position: center center !important;

background-repeat: no-repeat !important;

}

/*ICON BAR end*/

/*ICON BAR BACKGROUND begin - background for the icon bar*/

#new_post {

background-color: cddbdb(255, 255, 255, 1.0) !important;

}

/*ICON BAR BACKGROUND end*/

/*AVATAR ARROW begin - arrow to the left of the icon bar*/

#new_post .arrow {

opacity: 1.0 !important;

margin-left: 0px !important;

}

/*AVATAR ARROW end*/

/*CONTENT begin - background for posts, icon bar, sidebar*/

#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {

background: cddbdb(44,71,98, 1.00) !important;

}

/*CONTENT*/

/*POST BACKGROUND begin*/

#posts LI[class*=" post"] {

background: cddbdb(255, 255, 255, 1.0) !important;

}

/*POST BACKGROUND end*/

/*SELECTION ARROW begin - points to dashboard selection*/

.selection_nipple.white, .selection_nipple:not([class="selection_nipple white"]) {

background: none !important;

width: 0px !important;

height: 0px !important;

margin-top: 0px !important;

border-bottom: solid 11px cddbdb(255,255,255,1.0) !important;

border-right: 11px solid transparent !important;

border-left: 11px solid transparent !important;

}

.selection_nipple:not([class="selection_nipple white"]) {

border-bottom: solid 11px cddbdb(44,71,98, 1.0) !important;

}

/*SELECTION ARROW end*/

/*GREEN BUTTONS begin - blue/green buttons from sidebar to transparent*/

li.selected, li.recessed, a.active, div.post_filter_tabs a {

background: none !important;

}

/*GREEN BUTTONS end*/

/*RADAR begin - removes radar blue frame - softens appearance*/

.post.photo_post .photo, .post.photo_post .video_thumbnail {

-moz-box-shadow: 1px 1px 3px cddbdb(0, 0, 0, 0.0) !important;

border-radius: 12px !important;

}

.content.is_media, .mask.top, .mask.bottom {

background: none !important;

}

/*RADAR end*/

/*NEW CONTENT begin - text post message body background*/

.mceContentBody {

color: cddbdb(0, 0, 0, 1.0) !important;

background: cddbdb(255,255,255, 1.0) !important;

}

/*NEW CONTENT end*/

/*DO NOT DELETE THIS BRACKET*/}
