css:
@namespace url(http://www.w3.org/1999/xhtml);



@-moz-document url-prefix("http://omegle.com/") {

#header, #intro, .logwrapper, .disconnectbtnwrapper, .sendbtnwrapper, .chatmsgwrapper { border: 0px solid #CCC; }

#logo {

   background:transparent url(http://i55.photobucket.com/albums/g136/macgyver89/omegle.png) no-repeat;

   height:0;

   overflow:hidden;

   padding-top: 57px; /* height of logo for screen */

   width: 235px; /* width of logo for screen */

}

#tagline {

   background:transparent url(http://i55.photobucket.com/albums/g136/macgyver89/talk_to_strangers.png) no-repeat;

   height:0;

   overflow:hidden;

   padding-top: 57px; /* height of logo for screen */

   width: 220px; /* width of logo for screen */

}

p, div, form, h2,  body {color:lightgrey !important; background: url(http://i.imgur.com/SxzMp.jpg) #222222 fixed repeat left top !important;}



a, a:link, a:hover {color:white !important;}



.logbox {visibility:hidden !important;}



.logbox a, .logbox a:link, .logbox .youmsg, .logbox .strangermsg, .logbox input {visibility:visible !important;}



.youmsg {color:#99CCFF !important;}



.youmsg .msgsource {color:#3F9FFF !important;}



.strangermsg {color:#99CCFF !important;}



.strangermsg .msgsource {color:#FF8000 !important;}



.logitem input {color:black !important; }



img {opacity:1.0 !important;}



#chatbutton img {visibility:visible !important;}



.controlwrapper input {color:white !important; background-color:#1F334C !important;}



.statuslog {visibility:visible !important;}



.chatmsg {color:black !important;}



}
