css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("flickr.com") {

/* ==== FLICKR WIDESCREEN - (GM) Flickr SET view 150x150 thumbnails v.2 ==== */

/* SET - THUMBNAIL VIEW - THUMBNAIL CONTAINER - === */
#ViewSet #setThumbs.sq #photo-display-container {
    width: 1890px !important;
margin-left: -230px !important;
}

/* SET - THUMBNAIL VIEW - THUMBS - === */
#ViewSet #setThumbs.sq #photo-display-container .photo-display-item {
    height: 150px !important;
    width: 150px !important;
margin-bottom: 30px !important;
margin-left: -12px !important;
margin-right: 17px !important;
}


/* ==================== */
/* SET - THUMBNAIL - "USER . Set" - === */
#setCrumbs {
    margin-left: 1195px !important;
    width: 332px !important;
}

/* ==== THUMBNAILs / THUMBs / METAs ==== */
/* COR - THUMBNAIL - CLEARFIX (position/width) - (clear the HORIZONTAL SCROLL BAR) - A VOIR (USER SET PAGE) - 
clear: both !important;
float: none !important;
overflow: hidden !important;
overflow-y : scroll !important;
overflow-x : hidden !important;
=== */ 
#main.clearfix  {
    display: inline-block !important;
    position: relative !important;
    width: 1895px !important;
min-height: 4500px !important;
overflow: hidden !important;
overflow-y : hidden !important;
overflow-x : hidden !important;
}

/* COR - THUMBNAILs BUTTON - VIEWS OPTIONS - GENERAL - === */
.searchified-options .searchified-options-view {
    position: absolute !important;
    display: inline-block !important;
    left: 66.5% !important;
    margin-top: -43px !important;
    z-index: 5000 !important;
}
.searchified-options .searchified-options-sort {
    position: absolute !important;
    display: inline-block !important;
    left: 1260px !important;
    margin-top: -75px !important;
}

/* ==== SETs ====== */

/* SET - USER SETS PAGE - SETS THUMBS + MARGIN LEFT for .Sets.whitespaceless  - === */
.Sets.whitespaceless  {
    margin-right: 20px !important;
}
.Sets  {
    margin-left: 54px !important;
width: 100px !important;
margin-right: 10px !important;
}
/* SET - USER SETS PAGE - SETS TITLES + NBR - === */
.Sets>h4 ,
.Sets p{
    font-size: 15px;
    font-weight: bold;
    margin-bottom: 0;
    margin-left: -30px !important;
    width: 150px !important;
text-align: center !important;
}


/* SET - BLOCK DESCRIPTION ON HOVER - === */
#ViewSet > .vsDetails {
    position:absolute !important;
    width: 252px !important;
    min-height :750px !important;
    margin-left:-738px !important;
}
#ViewSet > .vsDetails:hover {
    position: absolute !important;
    background: black !important;
    border-radius: 0 5px 5px 0  !important;
    min-height: 750px !important;
    margin-top: -20px  !important;
    left: 38.7% !important;
    padding: 10px 20px 0 10px  !important;
z-index: 500 !important;
}
#ViewSet > .vsDetails:before {
    position: relative !important; 
    display: inline-block !important;
    content: "SET Detail  ▼  " !important;
    text-align: right !important;
    -moz-transform: rotate(-90deg) !important;
    -moz-transform-origin: 100% 100% !important;
    background: none repeat scroll 0 0 rgba(0, 0, 0, 0.7) !important;
    border-bottom-left-radius: 5px !important;
    border-bottom-right-radius: 5px !important;
    width: 252px !important;
    top: -20px !important;
    margin-left : 505px !important;
    opacity: 0.6 !important;
}
#ViewSet > .vsDetails:hover:before {
    margin-left : -525px !important;
}

/* SETs (choice) - SQUARE (sq) - "THUMBNAIL VIEW" - WITH SCRIPT - 3 ROWS (YOU CAN COMMENT/UNCOMMENT ) - 
#setThumbs {
    width: 1890px !important;
    margin-left: -444px !important;
} === */

/* SETs (choice) - SQUARE (sq) - "THUMBNAIL VIEW" - WITH SCRIPT - 6 ROWS (YOU CAN COMMENT/UNCOMMENT )  - === */
#setThumbs {
    width: 1290px !important;
}
#setThumbs.sq .photo-display-item {
    height: 80px !important;
    margin-bottom: 19px !important;
    margin-right: 30px !important;
}

/* ==== SETs - MEDIUM (md) - "DETAIL VIEW" ==== */

/* SET - MEDIUM (md) - "DETAIL VIEW" -
#ViewSet.set-view-list .md .photo-display-container - === */

/* SET - MEDIUM (md) - "DETAIL VIEW" - THUMBNAIL CONTAINER - === */
#ViewSet.set-view-list .md  {
    margin-bottom: 0;
    margin-right: auto;
    margin-top: 0;
    overflow: visible !important;
    padding: 0;
    width: 190% !important;
}
/* SET - MEDIUM (md) - "DETAIL VIEW" - THUMBS - === */
#ViewSet.set-view-list .md #photo-display-container .photo-display-item {
    display: inline-block;
    float: none;
    margin: 0 -10px 10px 30px !important;
    vertical-align: top;
}
/* SET - MEDIUM (md) - "DETAIL VIEW" - META - === */
#ViewSet.set-view-list .md.photo-display-container .photo-display-item .StreamView {
    font-size: 12px;
    line-height: 15px;
    margin-bottom: 0;
    margin-top: 5px;
    width: 237px !important;
}
/* POOL/FAV/SET - MEDIUM (md) - THUMBS - === */
#ViewSet.set-view-list .md.photo-display-container.md .photo-display-item {
    width: 15.28% !important;
}

/* ============================== */

/* SCRIPT "Number of FAVS on Photostream Pages and Pool Pages" - SET- SQUARE (sq) - "THUMBNAIL VIEW"  - GENERAL - 
    background:none repeat scroll 0 0 rgba(0, 0, 0, 0.6)
=== */
#setThumbs.sq [id^="photo-story-stats-"] {
    display : inline  !important;
    width: 520px !important;
    top : 2px  !important;
    padding:0 5px 0 8px !important;
    font-size: 10px !important;
    text-align : center  !important;
    white-space:nowrap !important;
}
#setThumbs.sq [id^="photo-story-stats-"] a {
    display:inline !important;
    width:20px !important;
    font-size: 10px !important;
    margin-top: -4px !important;
    margin-left:-5px !important;
}

/* SCRIPT "Number of FAVS on Photostream Pages and Pool Pages" - SET - SQUARE (sq) - "THUMBNAIL VIEW" - NBR FAV - === - 
#setThumbs.sq a[id^="nof_favs_"]{
    position: relative !important;
    display: inline-block !important;
    width:20px !important;
    height: 14px;
    top: -4px !important;
    margin-left:-7px !important;
    text-align: right !important;
    text-decoration: none;
}  === *



/* === end ==== */

}
