css:
@-moz-document domain("mail.ru") {
.profile .contentBlock .content .photoBlock .photo {
border: 1px solid #DDD!important;
background-color: #F7F7F7!important;
}
.profile .contentBlock .avatarBlock {
border: 1px solid #DDD!important;
background-color: #F7F7F7!important;
}
#mailikiSum, #showPaymentWindow, .mf_mb10,  .portal-menu__banner, .mf_smsTop.mf_ohd {
display: none!important;
}

.publisher, .publisher2 {
border: 1px solid #DDD!important;
background-color: #F7F7F7!important;
}
.publisher .publ_navig {
background-color: #F7F7F7!important;
}
.publisher .publ_textarea {
background: white!important;
padding: 3px!important;
border: 1px solid #C0CAD5!important;
}
.mf_navEl a, .other, #profileMenu a, .mf_iWorld a{
text-decoration: none!important;
}
.mf_navMenu {
border-right: none!important;
}
.publ_attaches i:hover {
height: 19px!important;
padding-left: 5px!important;
padding-right: 8px!important;
background: none!important;
border: none!important;
border-radius: 0!important;
-moz-border-radius: 0!important;
-webkit-border-radius: 0!important;
}
.publ_attaches i:hover a{
color: #F26D00!important;
}
.messenger .users {
border: none!important;
background: none!important;
}
.messenger .users h2{
display: none!important;
}
.messenger .message, .messenger .restricted {
border: 1px solid #DDD!important;
background-color: #F7F7F7!important;
}
.balloon-counter {
top: 0px!important;
right: -8px!important;
}

.comment_block {
border: 1px solid #DDD!important;
background-color: #F7F7F7!important;
}
.portal-menu__buttons__link, .portal-menu__buttons__link:active, .portal-menu__buttons__link:focus, .portal-menu__buttons__link:hover, .portal-menu__buttons__link:link, .portal-menu__buttons__link:visited {
background: none!important;
padding: 12px 10px 15px!important;
height: 13px!important;
margin: 0px!important;
font-weight: bold;
font-size: 11px;
color: #E8EFF7;
text-shadow: 0px 1px 0px #416389;
}

#publ_submit{
background: none!important;
}
#publ_submit span{
background: none!important;
color: #333!important;
padding: 2px 5px 0 5px!important;
}
}
