css:
@-moz-document domain("duckduckgo.com") {
body
{
width:50% !important;
background: #eee url('http://subtlepatterns.com/patterns/gradient_squares.png');
padding: 0 25% !important;
}

form
{
text-align: center;
}

.header
{
text-shadow: 0px -1px 3px #030303;
position: relative;
left: 35%;
}

.result-snippet
{
color: #3C3C3C;
font-size:90% !important;
}

.link-text{
color: #518FFF !important;
}

a
{
color: #1F5A81 !important;
}
}
