@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);

#appmenu-button {
background-image: none !Important;
text-shadow: 0 0 3px #ff0000 !important;
background: -moz-linear-gradient(top, #000, #3311ff, #000) #000 no-repeat !important;
color:#ff7518!important;
border-radius: 0px 15px 0px 15px !important;
border: 2px solid #3399ff !important;}

#appmenu-button:hover{
background-image: none !Important;
text-shadow: 0 0 3px #ff2510 !important;
background: -moz-linear-gradient(top, #000, #555, #000) #000 no-repeat !important; 
color:#ff0000!important;
border-radius: 15px 0px 15px 0px !important;
border: 2px solid #3333ff !important;}
