css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("serebiiforums.com") {

    html {background: url('http://img507.imageshack.us/img507/218/gohansupersaiyan2.jpg') fixed !important;}
    
    div {border-left-color: transparent !important;border-right-color: transparent !important;border-top-color: transparent !important;border-bottom-color: transparent !important;background-color: transparent !important;color: rgb(255, 255, 2) !important;}

    ul {background-color: transparent !important;background-image: transparent !important;}
    
    a {color: rgb(255, 255, 255) !important;background-color: transparent !important;background-image: transparent !important;}
    
    h3 {background-color: transparent !important;background-image: transparent !important;border-left-color: transparent !important;border-right-color: transparent !important;border-top-color: transparent !important;border-bottom-color: transparent !important;}
    
    h4 {background-color: transparent !important;background-image: transparent !important;}
    
    li {border-left-color: transparent !important;border-right-color: transparent !important;border-top-color: transparent !important;border-bottom-color: transparent !important;background-color: transparent !important;}
    
    textarea {background-color: rgb(0, 0, 0) !important;}
    
    span {background-color: rgb(0, 0, 0) !important;}
    
    input {background-color: rgb(0, 0, 0) !important;}
    
    blockquote {background-color: transparent !important;}
    
    h2 {background-color: transparent !important;background-image: transparent !important;}
    
    label {color: rgb(255, 255, 0) !important;}
    
    legend {color: rgb(255, 255, 0) !important;}
    
    p {color: rgb(255, 255, 0) !important;}
    
    fieldset {background-color: transparent !important;}
}
