@-moz-document regexp(".*ddt\\.game321\.com/Default\.aspx.*") {
    table[height="100%"]
    {
    	margin: 0px auto !important;
    	padding: 0px !important;
    	background-image: url("http://i48.tinypic.com/1iz9s6.png") !important;
    	background-repeat: no-repeat !important;
    	background-position: center center !important;
    	position:relative !important;
    	height:720px !important;
    	width:1100px !important;
    }
	td[valign="middle"] {
		position:absolute !important;
		top:30px !important;
		left:50px !important;
	}
}

@-moz-document regexp(".*passport\\.game321\.com/api/webgame/ddtank/index\.php.*") {
    iframe#ddt_game, div.body_center
    {
        height:720px !important;
        width:1100px !important;
    }
    div#top, div#addthis_fb
    {
    	display: none !important;
    }
}
