css:
@-moz-document domain('docs.google.com') {

	#docs-editor {

background: url(http://i.imgur.com/MYjjy.png) !important;

}



#kix-ruler {
background: #fff;
}

.docs-title-inner {font-family: Helvetica !important;}

.docs-menubar {height:28px !important;}

#docs-toolbar-wrapper {
	border-top: 1px solid #E3E3E3 !important;
}

#docs-chrome {
	background-image: -moz-linear-gradient(center top , #EEEEEE, #F5F5F5) !important;
	background-image: -webkit-linear-gradient(top, #EEEEEE, #F5F5F5) !important;
	color: #3D3D3D !important;
}



.docs-presence-widget-normal .docs-presence-widget-inner {
    background: none repeat scroll 0 0 transparent !important;
    border-color: #DFDFDF !important;
    color: #666666 !important;
}

}
