css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("fromthepavilion.org") {

body#phpbb>div#wrap
{background-color: white !important;
width: 960px !important;
margin:0 auto !important;
border-radius: 20px;
margin-top: -20px !important;
margin-bottom: 40px !important;
padding-top: 0px !important;

}

form#search {
position: relative !important;
margin-top: 145px !important;
}

div#site-description>p {display:none !important;}

body#phpbb>div#wrap>div#page-header>div.headerbar
{background-image: url("http://img36.imageshack.us/img36/2325/forumbar.png") !important;
height: 228px !important;}

div#site-description>a#logo {
position: relative !important;
top: 90px !important;
left: 660px !important;
height: 20px !important;
width: 100px !important;}

div#site-description>a#logo>img[src='./styles/prosilver/imageset/site_logo.gif']{
position: relative !important;
top: -80px !important;
left: -660px !important;
height: 200px !important;
width: 0px !important;
padding-left: 210px !important;
padding-top: 2px !important;
background-image: none  !important;
}



div#site-description>h1 {display: none !important;}
div#site-description>p.skiplink {display: none !important;}
div#site-description>p 
{padding-top: 25px !important;
padding-left: 75px !important;
font-size: 16px !important;}

form#search
{position: relative !important;
top: -20px !important;}

div#page-body a {color: rgb(49, 152, 185) !important;}
div#page-body>div.forabg>div.inner>ul.topiclist>li.header a {padding-left: 5px !important; color: white !important;}
div#page-body a.username-coloured {color: green !important;}

div#wrap>div#page-body div.forabg {
background-color: rgb(49, 152, 185) !important;
background-image: none !important;}

div#wrap>div#page-body div.forabg>div.inner
{
background-color: rgb(49, 152, 185) !important;
}

div#wrap>div#page-body>div.forumbg{
background-color: rgb(49, 152, 185) !important;
background-image: none !important;
}

div#wrap>div#page-body>div.forumbg>div.inner>ul.topiclist.topics{
background-color: white !important;
background-image: none !important;
}

div#wrap>div#page-body div.forabg>div.inner>ul.topiclist.forums
{
background-image: none !important;
}

div#wrap>div#page-header>div.navbar
{
background-color: rgb(254,152,15) !important;
max-width: 575px !important;
position: relative !important;
left: 364px !important;
top: -15px !important;}

div#wrap>div#page-header>div.navbar a {color: white !important;}


div#page-footer>div.navbar {background-color: rgb(254,152,15) !important;}
div#page-footer>div.navbar a {color: white !important;}
div#page-footer>div.navbar li.rightside {color: white !important;}


div#page-header>div.navbar{color: white !important;}
div#page-body>p {color: black !important;}
li.row:hover{background-color: rgb(254,152,15) !important;}


}
