css:
@-moz-document domain("groups.yahoo.com") {

/* Make background non-white */
body,.ygrp-header,.margin,.msgnum,.message,.info,.ygrp-blank,.msg,.ygrp-contentblock,.ygrp-container-border,.top,.bd,.doc-layout-head,div,table,.datatable th,.datatable td
{background:WhiteSmoke !important;}

/* Top: Remove banners */
iframe[src*="ad.yieldmanager"],#yahoo,#ygmapromo,#pa,#ygrp-title,#ygrp-sky,#yahoo,
.CAN_ad,.ygrp-onbg
{display:none !important;}

/* Top: Reduce white space */
.top,.bd,.doc-layout-head,.searcharea
{padding:0 !important;margin:0 !important;}
.msgnum,.message,.date
{padding-top:0 !important;padding-bottom:2px !important;}
.date br{display:none !important;}
/* Main: Remove junk */
#ygrp-sponsored-links,#doc-foot,#ygrp-gtips,#ygrp-mgl-promo,img[src*="check"],.footview,.info 
{display:none !important;}

/* rhs */
.ygrp-right-col
{display:none !important;}
}
