css:
@-moz-document domain("ptchan.net") {
	body {
		background: #FFFFFF;
		color: #000000;
	}

	hr {
		border-top: 1px solid #000000;
	}
	
	#watchedthreads {
		background-color: #E8E8E8 !important;
	}

	.logo {
		color: #000000;
	}

	.logo img {
		border: 1px solid #808080;
	}

	.postblock {
		background: #CFCFCF;
		color: #000000;
		border: 1px solid #E6E6E6;
	}

	.postername, .commentpostername {
		color: #3B3B3B;
	}

	td.reply, td.highlight {
		border: 1px solid #DEDEDE;
	}

	.reply {
		background: #FFFFFF;
		color: #000000;
	}

	.highlight {
		background: #CFCFCF;
		color: #000000;
	}

	.replymode {
		background: #000000;
	}

	.navbar {
		color: #808080 !important;
	}

	.navbar a, .adminbar a {
		color: #000000;
	}
}
