css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("4chan.org") {

    hr {
        display: none !important;
    }
    
    div.boardBanner {
        display: none !important;
    }
    
    div.stylechanger {
        display: none !important;
    }
    
    div#absbot {
        display: none !important;
    }
    
    div#boardNavDesktop {
        display: block !important;
		position: fixed !important;
		left: 0 !important;
		right: 0 !important;
		top: 0 !important;
		background: #533D51 !important;
		color: #FFFFFF !important;
		margin-left: auto;
		margin-right: auto;
		font-size: 7pt !important;
	}
	
	div#boardNavDesktopFoot {
	   display: none !important;
	}
	
	div#boardNavDesktop a {
	   color: #FFFFFF !important;
	}
	
	#navtopr, #navbotr {
	   display: none !important;
	}
	
	body {
	   font-family: verdana !important;
	   font-size: 9pt !important;
	   padding-top: 10px;
	   background: #FFD8ED url(http://s10.postimage.org/6qiqb082x/heart.jpg) fixed bottom right no-repeat !important;
	   color: #533D51 !important;
	}
	
	tr.rules td {
	   display: none !important;
	}
	
	table {
	   border-spacing: 0px !important;
	}
	
	div.post div.postInfo span.postNum a {
	   color: #FF0088 !important
	}
	
	.globalMessage {
	   display: none !important;
	}
	
	#updater:hover, div#thread_filter.autohide:hover, .box-outer, #preview-tooltip-nws, #preview-tooltip-ws, #disclaimer-dialog, .yuimenu .bd, .reply, #backwash_tooltip {
    background-color: #F0A5E9 !important;
    border: 1px dashed #FFFFFF !important;
    border-radius: 8px 8px 8px 8px;
    color: #533D51 !important;
    }
    
    a.quoteLink, a.quotelink {
    color: #FF0088 !important;
    }
    
    div.pagelist {
        background: none repeat scroll 0 0 #E2A5FF !important;
        color: #FFFFFF !important;
    }
    
    a, a:visited {
    color: #D573FF !important;
    }
    
    div.post div.postInfo span.nameBlock span.name {
    color: #FF358D !important;
    }
    
    blockquote > span.quote {
    color: #A600FF !important;
    }
    
    a.replylink, div#absbot a {
    color: #FF00C8 !important;
    }
    
    table.postForm > tbody > tr > td:first-child {
        background-color: #BC47ED !important;
    }
    
    div.postingMode {
        background-color: #F0A5E9 !important;
    }
}
