css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("forums.steampowered.com") {

.page { background:#424242 !important; }

HTML { background:#424242 !important; }

body { background:#424242 !important; }

.alt1, .alt1Active, .alt2, .alt2Active { background:#000 !important; border-color:#323232 !important; color:#d0d0d0 !important; font-weight:normal !important; }

.thead { background:#000 !important; border-color:#323232 !important; }

.tfoot { background:#000 !important; border-color:#323232 !important; }

.tcat { background:#000 !important; border-color:#323232 !important; color:#d0d0d0 !important; font-weight:normal !important; }

.vbmenu_control a:link, .vbmenu_control_alink, .vbmenu_control a:visited, .vbmenu_control_avisited { font-weight:normal !important; color:#d0d0d0 !important; }

#content { font-family:Calibri,Verdana,Arial,Helvetica,sans-serif !important; font-size-adjust:.6 !important; }

li { font-size:1em !important; line-height:1.5em !important; }

ol>li { list-style-type:decimal !important; }

}
